  top: 200px;
  background-color: #555
}

descriptor.controllerScript:
self.onInit = function() 
{
    var menu1Enable = self.ctx.settings.enableLink1;
    var menu2Enable = self.ctx.settings.enableLink2;
    var menu3Enable = self.ctx.settings.enableLink3;
    var menu4Enable = self.ctx.settings.enableLink4;
    var menu1Title = self.ctx.settings.content;
    document.getElementById("first").innerHTML = menu1Title;
    if (menu1Enable === false)
    {
        document.getElementById("first").style.visibility = "hidden";
    }
    var menu2Title = self.ctx.settings.content2;
    document.getElementById("second").innerHTML = menu2Title;
    if (menu2Enable === false)
    {
        document.getElementById("second").style.visibility = "hidden";
    }
    var menu3Title = self.ctx.settings.content3;
    document.getElementById("third").innerHTML = menu3Title;
    if (menu3Enable === false)
    {
        document.getElementById("third").style.visibility = "hidden";
    }
    var menu4Title = self.ctx.settings.content4;
    document.getElementById("fourth").innerHTML = menu4Title;
    if (menu4Enable === false)
    {
        document.getElementById("fourth").style.visibility = "hidden";
    }    
    var menu1Link = self.ctx.settings.link1;
    var menu2Link = self.ctx.settings.link2;
    var menu3Link = self.ctx.settings.link3;
    var menu4Link = self.ctx.settings.link4;

    document.getElementById("first").href = menu1Link;
    document.getElementById("second").href = menu2Link;
    document.getElementById("third").href = menu3Link;
    document.getElementById("fourth").href = menu4Link;

}

